<script setup>
import { ref, computed } from 'vue';
import SidebarChatItem from '../components/ChatSideBarComponet.vue';
import ChatDetailComponent from '../components/ChatDetailComponent.vue';

const chats = ref([
  {
    groupId: "8_9",
    groupName: "8_9",
    lastMessage: "Huong dep trai",
    groupBackground: "data:image/webp;base64,",
    lastActive: "2024-08-15T01:05:17.725Z",
    messageUnreadCount: null,
    online: false
  },
  {
    groupId: "66c54ccd48dce457c945fe34",
    groupName: "Huong dep trai vl",
    lastMessage: "Hello world",
    groupBackground: "data:image/webp;base64,",
    lastActive: null,
    messageUnreadCount: 100,
    online: true
  },
  // Add more chats if needed
]);

const selectedChat = ref(null);
const searchQuery = ref('');
const showCreateGroupDialog = ref(false);
const newGroupName = ref('');

const filteredChats = computed(() => {
  return chats.value.filter(chat => 
    chat.groupName.toLowerCase().includes(searchQuery.value.toLowerCase())
  );
});

const selectChat = (groupId) => {
  selectedChat.value = chats.value.find(chat => chat.groupId === groupId);
};

const createGroup = () => {
  if (newGroupName.value.trim()) {
    const newGroup = {
      groupId: Date.now().toString(), // Generate a unique ID
      groupName: newGroupName.value,
      lastMessage: null,
      groupBackground: null,
      lastActive: new Date().toISOString(),
      messageUnreadCount: 0,
      online: false
    };
    chats.value.push(newGroup);
    newGroupName.value = '';
    showCreateGroupDialog.value = false;
  }
};
</script>

<template>
  <div class="chat-layout">
    <div class="group">
      <div class="search-bar">
        <input type="text" v-model="searchQuery" placeholder="Tìm kiếm cuộc trò chuyện...">
      </div>
      <SidebarChatItem
        v-for="chat in filteredChats"
        :key="chat.groupId"
        :groupId="chat.groupId"
        :groupName="chat.groupName"
        :lastMessage="chat.lastMessage"
        :groupBackground="chat.groupBackground"
        :lastActive="chat.lastActive"
        :messageUnreadCount="chat.messageUnreadCount"
        :online="chat.online"
        @click="selectChat(chat.groupId)"
      />
      <div class="create-group-button" @click="showCreateGroupDialog = true">
        <span class="plus-icon">+</span>
      </div>
    </div>
    <div class="chat">
      <template v-if="selectedChat">
        <ChatDetailComponent :chat="selectedChat" />
      </template>
      <template v-else>
        <p>Vui lòng chọn một cuộc trò chuyện</p>
      </template>
    </div>
    <div v-if="showCreateGroupDialog" class="create-group-dialog">
      <h3>Tạo nhóm mới</h3>
      <input v-model="newGroupName" placeholder="Nhập tên nhóm">
      <button @click="createGroup">Tạo</button>
      <button @click="showCreateGroupDialog = false">Hủy</button>
    </div>
  </div>
</template>

